<?php    // $URL="https://salon.ypsbengaluru.in";    $URL="https://www.ypsbengaluru.com/svc/hello.php";    // $URL="https://www.google.com";    if (isset($_GET["site"])) { $URL = $_GET["site"]; }    $ch = curl_init();    echo "URL = $URL <br>\n";    curl_setopt($ch, CURLOPT_VERBOSE, 1);        curl_setopt($ch, CURLOPT_KEEP_SENDING_ON_ERROR, TRUE);    curl_setopt ($ch, CURLOPT_HTTPPROXYTUNNEL, TRUE);    curl_setopt ($ch, CURLOPT_SSL_VERIFYPEER, FALSE);    curl_setopt ($ch, CURLOPT_URL, $URL);		// curl_setopt($ch, CURLOPT_FOLLOWLOCATION, TRUE);    	// curl_setopt ($ch, CURLOPT_TIMEOUT, 600);	// curl_setopt ($ch, CURLOPT_CONNECTTIMEOUT, 600);		curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);		curl_setopt($ch, CURLOPT_HEADER, true);		// curl_setopt($ch, CURLOPT_HTTPHEADER, array('Access-Control-Allow-Origin : *'));	curl_setopt($ch, CURLOPT_HTTPHEADER, array('Origin: https://salon.ypsbengaluru.in'));    $result = curl_exec ($ch);        echo "<hr><br>\n";    echo 'Errors: ' . curl_errno($ch) . ' ' . curl_error($ch) . '<br><br>';    echo "<hr><br>\n";    curl_close ($ch);    print "result - $result";    echo "<hr><br>\n";?>